<?php

namespace CoreBundle\Repository;

use Doctrine\ORM\EntityRepository;
use CoreBundle\Services\Crud\BuildInterface\CrudInterface;
use CoreBundle\Services\ListHasTree\BuildInterface\ListHasTreeInterface;

/**
 * TemplateRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TemplateRepository extends EntityRepository implements CrudInterface, ListHasTreeInterface
{

    public function findAll($limit = null, $offset = null)
    {
        return $this->findBy(['isActive' => 1], ['idIncrement' => 'DESC'], $limit, $offset);
    }

    public function findOneById($id)
    {
        $em = $this->getEntityManager();
        $dql = "
            SELECT template
            FROM CoreBundle:Template template
            WHERE
            template.idIncrement = :id AND
            template.active = :active
            ";

        $query = $em->createQuery($dql);
        $query->setParameter('active', 1);
        $query->setParameter('id', $id);

        return $query->getOneOrNullResult();
    }

    public function findActiveTemplateById($id)
    {
        $em = $this->getEntityManager();
        $dql = "
            SELECT template
            FROM CoreBundle:Template template
            WHERE
            template.idIncrement = :id AND
            template.isActive = :active AND
            template.isActiveTemplate = :active
            ";

        $query = $em->createQuery($dql);
        $query->setParameter('active', 1);
        $query->setParameter('id', $id);

        return $query->getOneOrNullResult();
    }

